--delete any extra void shards you collected for quest 'voidridge'

number = 1;
i=1; 
for bag = 0,4,1 do 
 for slot = 1, GetContainerNumSlots(bag), 1 do
  local name = GetContainerItemLink(bag,slot);
  if name and string.find(name,"Void Ridge Soul Shard") then
   if i > number then PickupContainerItem(bag,slot);
   DeleteCursorItem();
   end;
  end;
  i=i+1; 
 end; 
end;

