Thread t = new Thread(() =>
{
              //Birds of a Feather Quest
             //Use Cage if Female Bird is Targeted

 uint itemId = 23485;
 int questId = 9397;

        
 while (robotManager.Products.Products.IsStarted)
 {
  if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
  {
   if (!Quest.HasQuest(questId))
    break;
   if (ObjectManager.Target.IsValid && ObjectManager.Target.IsAlive && ObjectManager.Target.Name =="Female Kaliri Hatchling" )
   {
    ItemsManager.UseItem(itemId);
   }
  }
  Thread.Sleep(500);
 }
});
t.Start();


// ObjectManager.Target.HealthPercent <= 25

		Thread t = new Thread(() =>
{

//purify hellboar meat for quest helbor the other white meat


 uint itemId = 23268;
 int questId = 9361;

        
 while (robotManager.Products.Products.IsStarted)
 {
  if (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
  {
   if (!Quest.HasQuest(questId))
    break;
   
   
   foreach (WoWItem item in Bag.GetBagItem())
				{
				
				if (item.GetItemInfo.ItemName == "Tainted Helboar Meat")
						{
							
						robotManager.Helpful.Keyboard.DownKey(wManager.Wow.Memory.WowMemory.Memory.WindowHandle, System.Windows.Forms.Keys.ShiftKey);
							Thread.Sleep(robotManager.Helpful.Others.Random(50, 150));
							ItemsManager.UseItem(itemId);
							Thread.Sleep(robotManager.Helpful.Others.Random(50, 150));
							robotManager.Helpful.Keyboard.UpKey(wManager.Wow.Memory.WowMemory.Memory.WindowHandle, System.Windows.Forms.Keys.ShiftKey);
							Thread.Sleep(robotManager.Helpful.Others.Random(50, 150));	
							
						}
				
				}

   

  }
  Thread.Sleep(500);
 }
});
t.Start();

